"use strict";

function convertStrToObj(str) {
  if (!str) {
    return {};
  }

  return JSON.parse(str);
} //获取cookie


var cookie_str = getCookie('carts') ? getCookie('carts') : ''; //转对象

var cookie_obj = convertStrToObj(cookie_str);
var tog = 0; //遍历对象

console.log(cookie_str);

for (var key in cookie_obj) {
  var good = cookie_obj[key];
  $('.cartList').append("\n        <tr class=\"goodinfo\" data-good-id = \"".concat(key, "\">\n            <td width=\"36\" height=\"33\">").concat(key, "</td>\n            <td width=\"554\" height=\"33\" style=\"text-align: left;\"><a href=\"javascripy:;\">").concat(good.name, "</a></td>\n            <td width=\"41\" height=\"33\">100ml</td>\n            <td width=\"49\" height=\"33\">500</td>\n            <td width=\"47\" height=\"33\">").concat(good.price, "</td>\n            <td width=\"29\" height=\"33\" class=\"num\">").concat(good.num, "</td>\n            <td width=\"59\" height=\"33\" class=\"total\">").concat(good.price * good.num, "</td>\n            <td width=\"70\" height=\"33\">\u8F6C\u5165\u6536\u85CF\u5939</td>\n            <td width=\"45\" height=\"33\"><a href=\"javascript:;\" class=\"del\">\u5220\u9664</a></td>\n        </tr>\n    "));
  tog += good.price * good.num;
}

$('.sho').html(tog);